import React,{Component} from "react";



export class Popup extends React.ComponentÂ {
    render() {
      return (
        <div className='popup'>
          <div className='popup_inner'>
            <h1>{this.props.text}</h1>
          <button onClick={this.props.closePopup}>close me</button>
          </div>
        </div>
      );
    }
  }
  export  class XX extends React.Component {
    constructor() {
      super();
      this.state = {
        showPopup: false
      };
    }
    togglePopup() {
        window.open('http://localhost:3000/admin/Grades', 'sharer', 'toolbar=0,status=0,width=548,height=325');
      this.setState({
        showPopup: !this.state.showPopup
      });
    }
    render() {
      return (
        <div className='app'>
          <h1>hihi</h1>
          <button onClick={this.togglePopup.bind(this)} >show popup</button>
          <button onClick={() => {alert('woooooooot?');}}>try me when popup is open</button>
          <p>Ganz viel inhalt.<br />Ganz viel inhalt..<br />Ganz viel inhalt.<br /></p>
          {this.state.showPopup ? 
            <Popup
              text='Close Me'
              closePopup={this.togglePopup.bind(this)}
            />
            : null
          }
        </div>
      );
    }
  };
